import React, { Component } from 'react';
import { connect } from 'react-redux';
import { getProducts } from './reducers/product/getProducts';
import ItemService from './service/ItemService'

class App extends Component {
    constructor(props) {
        super(props);
        this.state = {
            text: ''
        }
    }

    componentDidMount() {
        var cors_api_host = 'cors-anywhere.herokuapp.com';
        var cors_api_url = 'https://' + cors_api_host + '/';
        var slice = [].slice;
        var origin = window.location.protocol + '//' + window.location.host;
        var open = XMLHttpRequest.prototype.open;
        XMLHttpRequest.prototype.open = function() {
            var args = slice.call(arguments);
            var targetOrigin = /^https?:\/\/([^\/]+)/i.exec(args[1]);
            if (targetOrigin && targetOrigin[0].toLowerCase() !== origin &&
                targetOrigin[1] !== cors_api_host) {
                args[1] = cors_api_url + args[1];
            }
            return open.apply(this, args);
        };
        // var requestOptions = {
        //     method: 'GET',
        //     // 'Access-Control-Allow-Origin': "http://localhost:3000",
        //     // 'Access-Control-Allow-Headers': "*",
        //     redirect: 'follow'
        // };
        //
        // fetch("http://localhost:3000/https://www.aesop.com/au/api/v1/nav/shop", requestOptions)
        //     .then(response => response.text())
        //     .then(result => console.log(result))
        //     .catch(error => console.log('error', error));
        // fetch('https://www.aesop.com/au/api/v1/nav/shop', {
        // // fetch('http://jsonplaceholder.typicode.com/users', {
        //     headers: {
        //         'Content-Type': 'application/json',
        //         'Access-Control-Allow-Origin': '*'
        //     },
        // })
        //     .then(res => res.json())
        //     .then((data) => {
        //         console.log(data)
        //     })
        ItemService.getItemDetail()
            .then(res => {
                console.log(res)
            })
        this.props.getProducts()
    }


    render() {
        return (
            <div className="App">
                <div className="container">
                    <div className="text-center">
                        <h1>Aesop</h1>
                    </div>
                </div>
            </div>
        );
    }
}

const mapStateToProps = ({ products }) => {
    return {
        products: products
    }
}

export default connect(mapStateToProps , { getProducts })(App);
