import React, { Component } from 'react';
import CSVReader from 'react-csv-reader'
import {connect} from "react-redux";
import {getProducts} from "../reducers/product/getProducts";
import moment from 'moment'


class ImportButton extends Component {
    constructor() {
        super();
        this.state = {

        }
    }

    addBill = (newBill) => {
        this.props.addBill(newBill);
    }

    handleForce = (data) => {
        data.forEach( bill => {
            let newBill = {
                billTime: moment(bill.Time).format('YYYY-MM-DD'),
                billType: bill.Type,
                billCategory: bill.Category,
                billAmount: bill.Amount
            }
            this.addBill(newBill)
        })
        this.setState( {
            inputKey: Math.random()
        })
        return true;
    };

    render() {
        const parserOptions = {
            header: true,
            dynamicTyping: true,
            skipEmptyLines: true,
        };

        return (
            <div key={this.state.inputKey || ' '}>
                <CSVReader
                    cssClass="react-csv-input"
                    label="Import the bills from selected CSV file"
                    onFileLoaded={this.handleForce}
                    parserOptions={parserOptions}
                />
            </div>
        );
    }
}

const mapStateToProps = ({ bill }) => {
    return {
        bills: bill
    }
}

export default connect(mapStateToProps , { addBill: getProducts })(ImportButton);
