import {
  IMPORT_BILL,
} from '../../constant'

export const INITIAL_STATE = [];

const createBill = (payload) => {
  return {
    billTime: payload.billTime,
    billType: payload.billType,
    billCategory: payload.billCategory,
    billAmount: payload.billAmount
  }
}

const bill = (state = INITIAL_STATE, { type, payload }) => {
  switch (type) {
    case IMPORT_BILL: {
      return [
        ...state,
        createBill(payload)
      ];
    }
    default: return state;
  }
}

export default bill
