        //     },
        // })
        //     .then(res => res.json())
        //     .then((data) => {
        //         console.log(data)
        //     })
        // ItemService.getItemDetail()
        //     .then(res => {
        //         console.log(res)
        //     })
        // this.props.getProducts()
    }


    render() {
        return (
            <div className="App">
                <div className="container">
                    <div className="text-center">
                        <h1>Aesop</h1>
                    </div>
                </div>
            </div>
        );
    }
}

const mapStateToProps = ({ products }) => {
    return {
        products: products
    }
}

export default connect(mapStateToProps , { getProducts })(App);
